mapper.select:
SELECT
id,banner
FROM tb_curriculum WHERE state='上线' AND top=1;

SELECT c.id, c.title as name, c.learning_cycle, c.tag, tp.icon, th.title, th.nick_name FROM tb_curriculum c JOIN tb_curriculum_type tp ON tp.id=c.type_id JOIN tb_curriculum_teacher cth ON c.id=cth.curriculum_id JOIN tb_teacher th ON cth.teacher_id=th.id WHERE c.state='上线'
AND c.difficulty=#{difficulty}
AND c.title LIKE '%${keyword}%'
ORDER BY c.id DESC LIMIT #{start},#{length}
